USE autodiely_eshop;

-- TABLES
CREATE TABLE autodiely_eshop.users (
  _id INT AUTO_INCREMENT PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  email VARCHAR(255) NOT NULL UNIQUE,
  password VARCHAR(255) NOT NULL,
  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
) ENGINE=InnoDB;

CREATE TABLE autodiely_eshop.products (
  _id INT AUTO_INCREMENT PRIMARY KEY,
  name VARCHAR(255),
  description TEXT,
  cta TEXT,
  price DECIMAL(10,2),
  image_url VARCHAR(512),
  manufacturer JSON,
  bestseller BOOLEAN DEFAULT FALSE,
  category VARCHAR(100),
  subCategory JSON,
  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
) ENGINE=InnoDB;


CREATE TABLE autodiely_eshop.cart (
  _id INT NOT NULL AUTO_INCREMENT,
  user_id INT NOT NULL,
  product_id INT NOT NULL,
  manufacturer VARCHAR(255) NOT NULL,
  quantity INT NOT NULL DEFAULT 1,
  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (_id),
  FOREIGN KEY (user_id) REFERENCES autodiely_eshop.users(_id) ON DELETE CASCADE,
  FOREIGN KEY (product_id) REFERENCES autodiely_eshop.products(_id) ON DELETE CASCADE,
  UNIQUE KEY user_product_manufacturer (user_id, product_id, manufacturer)
) ENGINE=InnoDB;

CREATE TABLE autodiely_eshop.orders (
    order_number VARCHAR(50) PRIMARY KEY,
    user_id INT NOT NULL,
    first_name VARCHAR(100) NOT NULL,
    last_name VARCHAR(100) NOT NULL,
    email VARCHAR(255) NOT NULL,
    street VARCHAR(255) NOT NULL,
    city VARCHAR(100) NOT NULL,
    state VARCHAR(100) NOT NULL,
    zip_code VARCHAR(20) NOT NULL,
    country VARCHAR(100) NOT NULL,
    phone VARCHAR(20) NOT NULL,
    items JSON NOT NULL COMMENT 'Stores array of {product_id, manufacturer, quantity} objects',
    total_amount DECIMAL(10, 2) NOT NULL,
    payment_method VARCHAR(50) NOT NULL,
    status VARCHAR(50) NOT NULL DEFAULT 'Order Placed',
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    INDEX idx_user_orders (user_id)
);


CREATE TABLE autodiely_eshop.ke_branch (
  product_id INT,
  product_name VARCHAR(255),
  qty INT NOT NULL DEFAULT 0,
  manufacturer VARCHAR(255),
  FOREIGN KEY (product_id) REFERENCES autodiely_eshop.products(_id)
    ON DELETE CASCADE
    ON UPDATE CASCADE
) ENGINE=InnoDB;


CREATE TABLE autodiely_eshop.bl_branch (
  product_id INT,
  product_name VARCHAR(255),
  qty INT NOT NULL DEFAULT 0,
  manufacturer VARCHAR(255),
  FOREIGN KEY (product_id) REFERENCES autodiely_eshop.products(_id)
    ON DELETE CASCADE
    ON UPDATE CASCADE
) ENGINE=InnoDB;


CREATE TABLE autodiely_eshop.pp_branch (
  product_id INT,
  product_name VARCHAR(255),
  qty INT NOT NULL DEFAULT 0,
  manufacturer VARCHAR(255),
  FOREIGN KEY (product_id) REFERENCES autodiely_eshop.products(_id)
    ON DELETE CASCADE
    ON UPDATE CASCADE
) ENGINE=InnoDB;


CREATE TABLE autodiely_eshop.nr_branch (
  product_id INT,
  product_name VARCHAR(255),
  qty INT NOT NULL DEFAULT 0,
  manufacturer VARCHAR(255),
  FOREIGN KEY (product_id) REFERENCES autodiely_eshop.products(_id)
    ON DELETE CASCADE
    ON UPDATE CASCADE
) ENGINE=InnoDB;


CREATE TABLE autodiely_eshop.sc_branch (
  product_id INT,
  product_name VARCHAR(255),
  qty INT NOT NULL DEFAULT 0,
  manufacturer VARCHAR(255),
  FOREIGN KEY (product_id) REFERENCES autodiely_eshop.products(_id)
    ON DELETE CASCADE
    ON UPDATE CASCADE
) ENGINE=InnoDB;


CREATE TABLE autodiely_eshop.branch_orders (
  _id INT AUTO_INCREMENT PRIMARY KEY,
  branch VARCHAR(2) NOT NULL,
  order_number VARCHAR(20) NOT NULL UNIQUE,
  product_id INT NOT NULL,
  product_name VARCHAR(255) NOT NULL,
  manufacturer VARCHAR(255) NOT NULL,
  quantity INT NOT NULL,
  price DECIMAL(10,2) NOT NULL,
  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  customer_name VARCHAR(255),
  customer_email VARCHAR(255),
  customer_phone VARCHAR(20),
  notes TEXT,
  FOREIGN KEY (product_id) REFERENCES autodiely_eshop.products(_id)
);





